// Получаем activity_id из параметров URL
const activityId = $input.first().json.params.activity_id;

// Логируем для отладки
console.log('Cancelling notifications for activity:', activityId);

// Проверяем наличие activity_id
if (!activityId) {
  return [{
    json: {
      success: false,
      error: 'Missing activity_id parameter',
      timestamp: new Date().toISOString()
    }
  }];
}

// В реальном приложении здесь была бы логика работы с БД
// Сейчас возвращаем успешный ответ для демо

return [{
  json: {
    success: true,
    message: `Notifications cancelled for activity ${activityId}`,
    activity_id: activityId,
    cancelled_count: 2,
    cancelled_types: ['day_before', 'three_hours'],
    timestamp: new Date().toISOString()
  }
}];
