// Получаем activity_id из параметров URL
const activityId = $input.first().json.params.activity_id;

if (!activityId) {
  return [{
    json: {
      success: false,
      error: 'Missing activity_id parameter'
    }
  }];
}

// В реальном приложении здесь была бы логика отмены в БД
// Сейчас просто возвращаем успешный ответ

return [{
  json: {
    success: true,
    message: `Notifications cancelled for activity ${activityId}`,
    activity_id: activityId,
    cancelled_count: 2,
    timestamp: new Date().toISOString()
  }
}];
